import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { useAppContext } from "../Store/store";

export default function UpdateEvent() {
  const location = useLocation();
  const navigate = useNavigate();
  const { eventDispatch } = useAppContext();

  const event = location.state?.event;

  const [name, setName] = useState(event?.name || "");
  const [phone, setPhone] = useState(event?.phone || "");
  const [eventId, setEventId] = useState(event?.eventId || "");
  const [date, setDate] = useState(event?.date || "");
  const [locationField, setLocationField] = useState(event?.location || "");

  const handleUpdate = async () => {
    try {
      const res = await fetch(`http://localhost:3001/events/${event._id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          name,
          phone,
          eventId,
          date,
          location: locationField,
        }),
      });
      const data = await res.json();
      if (res.ok) {
        alert(data.msg || "Event updated successfully!");
        eventDispatch({ type: "events/updateEvent", payload: data.event });
        navigate("/profile"); // go back to profile
      } else {
        alert(data.error || "Update failed");
      }
    } catch (err) {
      console.error(err);
      alert("Server error. Please try again.");
    }
  };

  return (
    <div>
      <h2>Update Event</h2>
      <input
        value={name}
        onChange={(e) => setName(e.target.value)}
        placeholder="Name"
      />
      <input
        value={phone}
        onChange={(e) => setPhone(e.target.value)}
        placeholder="Phone"
      />
      <input
        value={eventId}
        onChange={(e) => setEventId(e.target.value)}
        placeholder="Event ID"
      />
      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
      />
      <input
        value={locationField}
        onChange={(e) => setLocationField(e.target.value)}
        placeholder="Location"
      />
      <button onClick={handleUpdate}>Save</button>
    </div>
  );
}
