import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import axios from "axios";
import "./MapView.css";

// Fix default icon issue in Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png",
  iconUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png",
  shadowUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png",
});

export default function MapView() {
  const [places, setPlaces] = useState([]); // All places from backend
  const [filteredPlaces, setFilteredPlaces] = useState([]);
  const [search, setSearch] = useState("");

  // Fetch places from backend
  useEffect(() => {
    async function fetchPlaces() {
      try {
        const res = await axios.get("http://localhost:3001/places"); // your API endpoint
        setPlaces(res.data.places);
        setFilteredPlaces(res.data.places);
      } catch (err) {
        console.error("Error fetching places:", err);
      }
    }
    fetchPlaces();
  }, []);

  // Handle search
  const handleSearch = (e) => {
    const value = e.target.value.toLowerCase();
    setSearch(value);
    const filtered = places.filter((p) => p.name.toLowerCase().includes(value));
    setFilteredPlaces(filtered);
  };

  return (
    <div className="map-view-container">
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search places..."
          value={search}
          onChange={handleSearch}
        />
      </div>

      <MapContainer
        center={[23.589, 58.382]} // Default coordinates (Oman)
        zoom={12}
        style={{ height: "80vh", width: "100%" }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; OpenStreetMap contributors"
        />

        {filteredPlaces.map((place) => (
          <Marker key={place._id} position={[place.lat, place.lng]}>
            <Popup>
              <h3>{place.name}</h3>
              {place.accessibility ? (
                <ul>
                  {place.accessibility.lift && <li>Lift ✅</li>}
                  {place.accessibility.ramp && <li>Ramp ✅</li>}
                  {place.accessibility.toilet && <li>Accessible Toilet ✅</li>}
                </ul>
              ) : (
                <p>No accessibility info</p>
              )}
              <p>{place.address || ""}</p>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
}
