import React from "react";
import { useAppContext } from "../Store/store";
import { useNavigate } from "react-router-dom";
import "./Profile.css";

export function Profile() {
  const { user, events, eventDispatch } = useAppContext();
  const navigate = useNavigate();

  const handleDelete = async (id) => {
    try {
      const res = await fetch(`http://localhost:3001/events/${id}`, {
        method: "DELETE",
      });
      if (res.ok) {
        eventDispatch({ type: "events/deleteEvent/fulfilled", payload: id });
      } else {
        alert("Failed to delete");
      }
    } catch (err) {
      console.error(err);
      alert("Server error while deleting");
    }
  };

  const handleEdit = (event) => {
    navigate("/update-event", { state: { event } });
  };

  return (
    <div className="profile-page">
      <h1>{user?.name}'s Registered Events</h1>
      {events.length === 0 ? (
        <p>No events registered yet.</p>
      ) : (
        <ul>
          {events.map((ev) => (
            <li key={ev._id}>
              {ev.name} - {ev.phone} - Event ID: {ev.eventId}
              <button onClick={() => handleEdit(ev)}>Edit</button>
              <button onClick={() => handleDelete(ev._id)}>Delete</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
