import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

// Fix default icon issue in Leaflet
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png",
  iconUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png",
  shadowUrl:
    "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png",
});

export default function Map() {
  const [places, setPlaces] = useState([]);
  const [filter, setFilter] = useState("");
  const [search, setSearch] = useState("");

  // Fetch dynamic data from server (MongoDB)
  useEffect(() => {
    const fetchPlaces = async () => {
      try {
        const res = await fetch("http://localhost:3001/places"); // API endpoint returning array of places
        const data = await res.json();
        setPlaces(data.places); // [{name, lat, lng, lift, toilet, ramp}]
      } catch (err) {
        console.error("Error fetching places:", err);
      }
    };
    fetchPlaces();
  }, []);

  // Filter places dynamically
  const filteredPlaces = places.filter(
    (p) =>
      (filter === "" || p[filter] === true) &&
      (search === "" ||
        p.name.toLowerCase().includes(search.toLowerCase()) ||
        p.address?.toLowerCase().includes(search.toLowerCase()))
  );

  return (
    <div>
      <div className="controls" style={{ padding: "10px" }}>
        <input
          type="text"
          placeholder="Search by name or address..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{ marginRight: "10px" }}
        />

        <select
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
          style={{ padding: "5px" }}
        >
          <option value="">All</option>
          <option value="lift">Lift</option>
          <option value="toilet">Toilet</option>
          <option value="ramp">Ramp</option>
        </select>
      </div>

      <MapContainer
        center={[23.588, 58.382]} // default coordinates (Oman)
        zoom={13}
        style={{ height: "80vh", width: "100%" }}
      >
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        {filteredPlaces.map((place) => (
          <Marker key={place._id} position={[place.lat, place.lng]}>
            <Popup>
              <h3>{place.name}</h3>
              {place.address && <p>{place.address}</p>}
              <p>
                Accessibility: {place.lift && "Lift "}{" "}
                {place.toilet && "Toilet "} {place.ramp && "Ramp"}
              </p>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
}
