//src/components/ApplyForm
import React, { useState } from "react";
import { ArrowLeft, Upload, CheckCircle } from "lucide-react";
import {
  AlertDialog,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogHeader,
  AlertDialogTitle,
} from "./ui/alert-dialog"; // keep this if you already have it, otherwise can replace with your own modal

export function ApplyForm({ onSubmit, onBack }) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [skills, setSkills] = useState("");
  const [showSuccess, setShowSuccess] = useState(false);

  const handleSubmit = () => {
    setShowSuccess(true);
    setTimeout(() => {
      setShowSuccess(false);
      onSubmit();
    }, 2000);
  };

  return (
    <div className="min-h-screen bg-white flex flex-col">
      {/* Header */}
      <div className="p-4 flex items-center gap-4 border-b border-[#F5F5F5]">
        <button className="p-2 rounded hover:bg-gray-100" onClick={onBack}>
          <ArrowLeft className="w-5 h-5" />
        </button>
        <h1 className="text-[#212121] text-lg font-semibold">Apply for Job</h1>
      </div>

      {/* Form Body */}
      <div className="flex-1 overflow-auto px-6 py-6">
        <div className="max-w-md mx-auto space-y-5">
          {/* Name */}
          <div>
            <label htmlFor="apply-name" className="block text-sm font-medium">
              Full Name
            </label>
            <input
              id="apply-name"
              placeholder="Enter your full name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="mt-1.5 w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-[#009688]"
            />
          </div>

          {/* Email */}
          <div>
            <label htmlFor="apply-email" className="block text-sm font-medium">
              Email
            </label>
            <input
              id="apply-email"
              type="email"
              placeholder="your.email@example.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="mt-1.5 w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-[#009688]"
            />
          </div>

          {/* Phone */}
          <div>
            <label htmlFor="apply-phone" className="block text-sm font-medium">
              Phone Number
            </label>
            <input
              id="apply-phone"
              type="tel"
              placeholder="+968 XXXX XXXX"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              className="mt-1.5 w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-[#009688]"
            />
          </div>

          {/* Skills */}
          <div>
            <label htmlFor="apply-skills" className="block text-sm font-medium">
              Skills & Experience
            </label>
            <textarea
              id="apply-skills"
              placeholder="Tell us about your relevant skills and experience..."
              value={skills}
              onChange={(e) => setSkills(e.target.value)}
              className="mt-1.5 w-full border border-gray-300 rounded px-3 py-2 min-h-[120px] focus:outline-none focus:ring-2 focus:ring-[#009688]"
            />
          </div>

          {/* Upload Resume */}
          <div>
            <label className="block text-sm font-medium">Upload Resume</label>
            <div className="mt-1.5 border-2 border-dashed border-[#009688]/30 rounded-lg p-8 text-center hover:border-[#009688] transition-colors cursor-pointer">
              <Upload className="w-12 h-12 text-[#009688] mx-auto mb-3" />
              <p className="text-[#212121] mb-1">Click to upload resume</p>
              <p className="text-[#212121]/60">PDF, DOC, or DOCX (Max 5MB)</p>
            </div>
          </div>
        </div>
      </div>

      {/* Submit Button */}
      <div className="p-6 border-t border-[#F5F5F5]">
        <button
          onClick={handleSubmit}
          className={`w-full py-2 rounded text-white ${
            !name || !email || !phone
              ? "bg-gray-300 cursor-not-allowed"
              : "bg-[#009688] hover:bg-[#00796B]"
          }`}
          disabled={!name || !email || !phone}
        >
          Submit Application
        </button>
      </div>

      {/* Success Message */}
      <AlertDialog open={showSuccess}>
        <AlertDialogContent className="max-w-sm">
          <AlertDialogHeader>
            <div className="flex justify-center mb-4">
              <div className="w-16 h-16 bg-[#009688]/10 rounded-full flex items-center justify-center">
                <CheckCircle className="w-10 h-10 text-[#009688]" />
              </div>
            </div>

            <AlertDialogTitle className="text-center">
              Application Sent Successfully!
            </AlertDialogTitle>

            <AlertDialogDescription className="text-center">
              We've received your application and will review it shortly. Good
              luck!
            </AlertDialogDescription>
          </AlertDialogHeader>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
}
