import React from "react";
import { useAppContext } from "../Store/store";
import { useNavigate } from "react-router-dom";
import { ArrowLeft } from "lucide-react";
import "./Profile.css";

export function Profile() {
  const { user, events } = useAppContext();
  const navigate = useNavigate();

  // Go back to home page
  const handleBack = () => navigate("/home");

  // Go to settings page for the selected event
  const handleSelectEvent = (event) => {
    navigate("/settings", { state: { selectedEvent: event } });
  };

  return (
    <div className="profile-page">
      <div className="profile-header">
        <button className="back-btn" onClick={handleBack}>
          <ArrowLeft size={20} /> Back
        </button>
        <h1>My Profile</h1>
      </div>

      {/* User Info */}
      <div className="user-info">
        <img
          src={user?.avatar || "https://via.placeholder.com/100"}
          alt="User Avatar"
          className="user-avatar"
        />
        <div className="user-details">
          <h2>{user?.name}</h2>
          <p>{user?.email}</p>
        </div>
      </div>

      {/* User Events */}
      <div className="user-events">
        <h3>My Events</h3>
        {events.length === 0 ? (
          <p>No events registered yet.</p>
        ) : (
          <ul>
            {events.map((ev) => (
              <li
                key={ev._id}
                className="event-card"
                onClick={() => handleSelectEvent(ev)}
              >
                <h4>{ev.name}</h4>
                <p>
                  <strong>Phone:</strong> {ev.phone}
                </p>
                <p>
                  <strong>Event ID:</strong> {ev.eventId}
                </p>
                <p>
                  <strong>Date:</strong> {ev.date || "Not set"}
                </p>
                <p>
                  <strong>Location:</strong> {ev.location || "Not set"}
                </p>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
