// Settings.js
import avatarImg from "../Images/avatar.png";
import React, { useEffect, useState } from "react";
import { useAppContext } from "../Store/store";
import { useNavigate } from "react-router-dom";
import { ArrowLeft, Edit2, Trash2 } from "lucide-react";
import "./Settings.css";

export function Settings() {
  const { user } = useAppContext();
  const navigate = useNavigate();
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);

  // ðŸ”¹ Fetch user events from MongoDB
  useEffect(() => {
    if (!user?.id) return;

    fetch(`http://localhost:3001/events/user/${user.id}`)
      .then((res) => res.json())
      .then((data) => {
        setEvents(data.events || []);
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setLoading(false);
      });
  }, [user]);

  // ðŸ”¹ Delete event
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this event?")) return;

    const res = await fetch(`http://localhost:3001/events/${id}`, {
      method: "DELETE",
    });

    if (res.ok) {
      setEvents((prev) => prev.filter((e) => e._id !== id));
      alert("Event deleted");
    } else {
      alert("Delete failed");
    }
  };

  // ðŸ”¹ Edit event
  const handleEdit = (event) => {
    navigate(`/updateevent/${event._id}`, { state: { event } });
  };

  return (
    <div className="settings-page">
      {/* HEADER */}
      <div className="settings-header">
        <button onClick={() => navigate("/home")} className="back-btn">
          <ArrowLeft size={18} /> Back
        </button>
        <h1>Settings</h1>
      </div>

      {/* USER INFO */}
      <div className="user-info">
        <img src={avatarImg} alt="avatar" className="user-avatar" />
        <h2>{user?.name}</h2>
        <p>{user?.email}</p>
      </div>

      {/* EVENTS */}
      <div className="user-events">
        <h3>My Events</h3>

        {loading ? (
          <p>Loading...</p>
        ) : events.length === 0 ? (
          <p>No events registered.</p>
        ) : (
          events.map((ev) => (
            <div key={ev._id} className="event-card">
              <h4>{ev.name}</h4>
              <p>
                <b>Phone:</b> {ev.phone}
              </p>
              <p>
                <b>Date:</b> {ev.date || "Not set"}
              </p>
              <p>
                <b>Location:</b> {ev.location || "Not set"}
              </p>

              <div className="event-actions">
                <button onClick={() => handleEdit(ev)}>
                  <Edit2 size={16} /> Edit
                </button>
                <button onClick={() => handleDelete(ev._id)}>
                  <Trash2 size={16} /> Delete
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
