import React from "react";
import {
  ArrowLeft,
  MapPin,
  DollarSign,
  Accessibility,
  Wifi,
  Home,
  Navigation,
} from "lucide-react";
import { useParams, useNavigate } from "react-router-dom";
import { useAppContext } from "../Store/store";
import "./JobDetails.css";

const jobData = {
  1: {
    title: "UI/UX Designer",
    company: "TechCorp",
    image:
      "https://images.unsplash.com/photo-1630283017802-785b7aff9aac?auto=format&fit=crop&w=1080&q=80",
    location: "Remote",
    salary: "$50k - $70k",
    type: "Full-time",
    description:
      "We are looking for a talented UI/UX Designer to join our growing team.",
    requirements: [
      "3+ years of experience",
      "Figma / Adobe XD",
      "Accessibility knowledge",
    ],
    accessibilityBenefits: [
      { icon: Accessibility, label: "Wheelchair accessible" },
      { icon: Wifi, label: "Remote work" },
      { icon: Home, label: "Flexible hours" },
    ],
  },
};

export function JobDetails() {
  const { jobId } = useParams(); // ‚úÖ from URL
  const navigate = useNavigate();
  const { user } = useAppContext(); // ‚úÖ logged-in user

  const job = jobData[jobId];

  // üîπ PASTE YOUR FUNCTION HERE
  const handleSaveJob = async () => {
    try {
      const res = await fetch("http://localhost:3001/save-job", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          userId: user._id,
          job: {
            id: Number(jobId),
            title: job.title,
            company: job.company,
            location: job.location,
            salary: job.salary,
            type: job.type,
          },
        }),
      });

      const data = await res.json();

      if (!res.ok) {
        alert(data.error);
      } else {
        alert("Job saved to profile ‚≠ê");
      }
    } catch (err) {
      alert("Server error");
    }
  };

  return (
    <div className="job-container">
      <div className="job-image-container">
        <img src={job.image} alt={job.company} className="job-image" />
        <button className="icon-btn back-btn" onClick={() => navigate(-1)}>
          <ArrowLeft />
        </button>
      </div>

      <div className="job-content">
        <h2 className="job-title">{job.title}</h2>
        <p className="job-company">{job.company}</p>

        <div className="job-info">
          <span className="job-type">{job.type}</span>
          <span className="job-location">
            <MapPin /> {job.location}
          </span>
          <span className="job-salary">
            <DollarSign /> {job.salary}
          </span>
        </div>

        <div className="accessibility-card">
          <p>Accessibility Benefits</p>
          <div className="benefits-list">
            {job.accessibilityBenefits.map((b, i) => {
              const Icon = b.icon;
              return (
                <div key={i} className="benefit-item">
                  <Icon />
                  <span>{b.label}</span>
                </div>
              );
            })}
          </div>
        </div>
      </div>

      <div className="job-footer">
        <button className="apply-btn" onClick={handleSaveJob}>
          ‚≠ê Save Job
        </button>

        <button className="apply-btn">
          <Navigation /> Apply Now
        </button>
      </div>
    </div>
  );
}
