import React from "react";
import { useAppContext } from "../Store/store";
import { useNavigate } from "react-router-dom";

export function Settings({ onLogout }) {
  const { events, eventDispatch } = useAppContext();
  const navigate = useNavigate();

  const handleDelete = async (id) => {
    try {
      const res = await fetch(`http://localhost:3001/events/${id}`, {
        method: "DELETE",
      });
      if (res.ok) {
        eventDispatch({ type: "events/deleteEvent", payload: id });
      } else {
        alert("Failed to delete");
      }
    } catch (err) {
      console.error(err);
      alert("Server error");
    }
  };

  return (
    <div className="settings-page">
      <h1>Settings - Manage Your Events</h1>
      <button onClick={() => navigate("/profile")}>Back</button>
      <button onClick={onLogout}>Logout</button>

      <ul>
        {events.map((ev) => (
          <li key={ev._id}>
            {ev.name} - {ev.phone} - Event ID: {ev.eventId}
            <button
              onClick={() =>
                navigate("/update-event", { state: { event: ev } })
              }
            >
              Edit
            </button>
            <button onClick={() => handleDelete(ev._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
