// Settings.js
import React, { useEffect, useState } from "react";
import { useAppContext } from "../Store/store";
import { useNavigate, Link } from "react-router-dom";
import { ArrowLeft, Edit2, Trash2 } from "lucide-react";
import "./Settings.css";

export function Settings() {
  const { user, events, eventDispatch } = useAppContext();
  const navigate = useNavigate();
  const [userEvents, setUserEvents] = useState([]);

  // 1. Filter events for the current user
  useEffect(() => {
    if (user && events) {
      const filteredEvents = events.filter((ev) => ev.userId === user.id);
      setUserEvents(filteredEvents);
    }
  }, [user, events]);

  // 2. Back to Home
  const handleBack = () => navigate("/home");

  // 3. DELETE EVENT
  const handleDelete = (id) => {
    // Dispatch Redux-like delete to store
    eventDispatch({ type: "events/deleteEvent/fulfilled", payload: id });
    // Update local state
    setUserEvents((prev) => prev.filter((ev) => ev._id !== id));
    alert("Event deleted");
  };

  // 4. UPDATE EVENT
  // Navigate to UpdateEvent page with state
  const handleUpdate = (event) => {
    navigate(`/updateevent/${event._id}`, { state: { event } });
  };

  return (
    <div className="settings-page">
      {/* HEADER */}
      <div className="settings-header">
        <button className="back-btn" onClick={handleBack}>
          <ArrowLeft size={20} /> Back
        </button>
        <h1>Settings</h1>
      </div>

      {/* USER INFO */}
      <div className="user-info">
        <img
          src="/user-avatar.png" // Make sure this image exists in public folder
          alt="User Avatar"
          className="user-avatar"
        />
        <div className="user-details">
          <h2>{user?.name || "User Name"}</h2>
          <p>{user?.email || "User Email"}</p>
          {user?.phone && <p>Phone: {user.phone}</p>}
        </div>
      </div>

      {/* USER EVENTS */}
      <div className="user-events">
        <h3>My Events</h3>
        {userEvents.length === 0 ? (
          <p>No events registered yet.</p>
        ) : (
          <ul>
            {userEvents.map((ev) => (
              <li key={ev._id} className="event-card">
                <h4>{ev.name}</h4>
                <p>
                  <strong>Phone:</strong> {ev.phone}
                </p>
                <p>
                  <strong>Event ID:</strong> {ev.eventId}
                </p>
                <p>
                  <strong>Date:</strong> {ev.date || "Not set"}
                </p>
                <p>
                  <strong>Location:</strong> {ev.location || "Not set"}
                </p>

                <div className="event-actions">
                  {/* Update button using handleUpdate */}
                  <button className="edit-btn" onClick={() => handleUpdate(ev)}>
                    <Edit2 size={16} /> Edit
                  </button>

                  {/* Delete button using handleDelete */}
                  <button
                    className="delete-btn"
                    onClick={() => handleDelete(ev._id)}
                  >
                    <Trash2 size={16} /> Delete
                  </button>
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}
